mapper.select:
SELECT c.idx, ct.type AS commtype, c.title, DATE_FORMAT(c.regDT, '%y.%m.%d') AS regDT, c.hit FROM t_community c JOIN t_communitytype ct ON c.commtype = ct.code WHERE c.delState = 0
AND c.commtype = #{commtype}
ORDER BY c.regDT DESC

SELECT c.idx, c.commtype, c.title, c.content, c.regDT, c.hit
FROM t_community c
WHERE c.idx = #{idx}
