mapper.update:
UPDATE T_order
SET recipient=#{recipient}, zipcode=#{zipcode}, addr=#{addr}, addrdetail=#{addrdetail}, request_memo=#{request_memo}, use_point=#{use_point}, tel=#{tel}
WHERE useridx= #{useridx} AND idx=#{order_idx}

UPDATE T_payment
SET successYN = 1,
paymentKey = #{paymentKey},
paytype = #{paytype},
ordername = #{ordername}
WHERE orderId = #{orderId}

UPDATE T_order
SET payState =1
WHERE idx=${param1}

UPDATE T_orderList
SET orderState =1
WHERE order_idx=${param1}

mapper.select:
SELECT pro_idx, amount
FROM T_orderList
WHERE order_idx = ${param1}

mapper.update:
UPDATE T_product
SET stock = stock - ${param2}
WHERE idx = ${param1}

mapper.select:
select paytype, orderId, createDT
from T_payment
where order_idx = ${param1}

mapper.update:
UPDATE T_payment
SET successYN = 0,
failReason = #{failReason}
WHERE orderId = #{orderId}
