SELECT COUNT(*) > 0
FROM likes
WHERE pro_idx = #{pro_idx} AND user_idx = #{useridx}

SELECT pro_idx, useridx, (CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END) AS liked
FROM t_prolike
WHERE pro_idx = #{pro_idx} AND useridx = #{useridx}

SELECT COUNT(*)
FROM t_prolike
WHERE pro_idx = #{pro_idx}

SELECT COUNT(*) > 0
FROM t_prolike
WHERE pro_idx = #{pro_idx} AND user_idx = #{useridx}
